import { StyleSheet } from "react-native";

const styles = StyleSheet.create({
    container: {
        flexDirection: 'row',
        width: '100vw',
        justifyContent: 'space-between',
        padding: 10
    },
    messageBox: {
        flexDirection: 'row',
    },
    time: {
        fontSize: 14,
        color: 'gray'
    }
});

export default styles;
