import { StyleSheet } from "react-native";

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#e5e5e5',
        padding: 10,
    },
    messageBox: {
        backgroundColor: 'white',
        marginRight: 50,
        borderRadius: 5,
    },
    time: {
        fontSize: 14,
        color: 'gray'
    }
});

export default styles;
