import React from "react";
import { StyleSheet } from "react-native";
import { View, Text } from "../Themed";
import { MaterialCommunityIcons, FontAwesome5, Entypo, Fontisto } from '@expo/vector-icons'
import { TextInput } from "react-native-gesture-handler";

const InputBox = () => {
    return (
        <View style={styles.container}>
            <View style={styles.mainContainer}>
                <FontAwesome5 name='laugh-beam' size={24} color='gray' />
                <TextInput />
                <Entypo name='attachment' size={24} color='gray' />
                <Fontisto name='camera' size={24} color='gray' />
            </View>
            <View style={styles.button}>
                hello
            </View>
        </View>
    );
}

export default InputBox;

const styles = StyleSheet.create({
    container: {
        flexDirection: 'row',
        backgroundColor: 'transparent',
    },
    mainContainer: {
        flexDirection: 'row',
        backgroundColor: 'white',
        padding: 10,
        margin: 10,
        borderRadius: 50,
    },
