import { StyleSheet } from "react-native";
import Colors from "../../constants/Colors";

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#e5e5e5',
        padding: 10,
    },
    messageBox: {
        backgroundColor: 'white',
        marginRight: 50,
        borderRadius: 5,
        padding: 10,
    },
    name: {
        color: Colors.light.tint,
        fontWeight: 'bold',
        marginBottom: 5,
    },
    message: {
        fontSize: 14,
        color: 'gray'
    },
    time: {
        fontSize: 9,
        color: 'gray'
    }
});

export default styles;
